        <h1 id="chronometre">{formatTime(time)}</h1>
      </div>

      <div className="btns">
        <button className="btn btnStr" onClick={start}> <i class="uil uil-play"> </i> Start</button>
        <button className="btnStp" onClick={stop}> <i class="uil uil-pause"> </i> Stop</button>
